mapper.select:
select c_pnum, c_qnt, cid, pname, pprice, psaleprice, pmainsfile, pstock, memail from mw_product mw, mw_cart mc where mw.pnum = mc.c_pnum
and memail = #{memail}
and memail is null

select count(*) from mw_cart
where c_pnum = #{c_pnum} 


		and memail = #{memail}


		and memail is null

mapper.update:
update mw_cart
set c_qnt = #{c_qnt}
where cid = #{cid}

mapper.select:
		select rno, oid, obuyer, odate, oproducts, ototal, ostatus, oinvoice
    	from(        
        	select rownum rno, oid, obuyer, to_char(odate, 'yyyy-mm-dd') odate, oproducts, ototal, ostatus, oinvoice
			from( 
        	    select oid, obuyer, odate, oproducts, ototal, ostatus, oinvoice
            	    from mw_order 
                	order by odate desc))
            	where rno between #{start} and #{end}

select oid, to_char(odate, 'yyyy-mm-dd') odate, ostatus 
from(
	select oid, odate, ostatus 
		from mw_order mo, mw_member mm 
			where mo.ob_email = mm.memail and memail = #{memail}
		order by odate desc)

mapper.update:
update mw_order
set ostatus = #{ostatus}
where oid = #{oid}

update mw_order
set oinvoice = #{oinvoice}
where oid = #{oid}
